import {FlatList, ActivityIndicator, Text, View} from 'react-native';

class PlateList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      isLoading: true,
      placeholder: "Loading",
    };
  }

  componentDidMount() {
    const Frisbee = require('frisbee');
    let res = 0;

    // create a new instance of Frisbee
    const api = new Frisbee({
      baseURI: 'http://localhost:8000/', // optional
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    });

    try{
    // this is a simple example using `.then` and `.catch`
    res = api.get("/restaurant/plates", {
        mode: 'no-cors',
      }).then(data => {return this.setState({"data": data})})

    this.setState({data: res['plates'], isLoading: false, placeholder: "ready"});
    console.log(this.state)
    }
    catch(error)
    {console.error(error)}

