import React, { Component } from "react";
import {FlatList, ActivityIndicator, Text, View, Button} from 'react-native';

class SideMenu extends Component {
  constructor(props) {
    super(props);
    this.state = {
        active: false,
        icon: '☰',
        width: 10,
    };
  }

  clickSidemenu(){
  if (this.state.active){

    this.style = {width: 100, height:100, color: 'blue'}
    this.setState({active: false})
  }else{

    this.style = {width: 10, height:10, color: 'red'}
    this.setState({active: true})
  }
  }


  render() {
  return (
        <Text style={{fontSize: 25}} onClick={this.clickSidemenu.bind(this)}>{this.state.active?'☓':'☰'}</Text>
  );
  }
}

export default SideMenu;
