import React, { Component } from "react";
import {FlatList, ActivityIndicator, Text, View} from 'react-native';

class PlateList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      isLoading: true,
      placeholder: "Loading",
    };
  }

  componentDidMount() {
    const Frisbee = require('frisbee');

    // create a new instance of Frisbee
    const api = new Frisbee({
      baseURI: 'http://localhost:8000/', // optional
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    });
    (async () => {
    try{
    // this is a simple example using `.then` and `.catch`
    const res = await api.get("restaurant/plates", {
        mode: 'no-cors',
      })

    this.setState({data: res['plates'], isLoading: false, placeholder: 'ready'});
    }catch(error){console.error(error)}
    })();
    /*
      .then(response => response.json())
      .then(response => {
        this.setState({data: response})
      })
      .catch((error) => console.error(error))
      .finally(() => {
        this.setState({ isLoading: false });
      });
      */
  }

  render() {
  const { data, isLoading, placeholder } = this.state;
    return (

    <View style={{ flex: 1, padding: 24 }}>
        <Text> {data[0].name} </Text>
        {isLoading ? <ActivityIndicator/> : (
          <FlatList
            data={data}
            keyExtractor={({ id }, index) => id}
            renderItem={({ item }) => (
              <Text>{item.name}, {item.price}</Text>
            )}
          />
        )}
      </View>
    );
  }
}

export default PlateList;
